import { getGroupById, getGroups } from "./groups";

const users = [
    {
        login: 'account1',
        password: "12345",
        name: '',
        groupIds: ['group1', 'group2']
    }
];

export function getUsers() {
    return users;
}

export function getUser(accountId) {
    const user = users.find(account => account.id === accountId);
    user.groups = user.groupIds.map(getGroup);  
    return user;
}

function getOwnedGroups(accountId) {
    const groups = getGroups();
    return groups.filter(group => group.ownerId === accountId);
}
