import { getGroupById, getGroups, getGroup } from "./groups";

const users = [
    {
        login: 'user1',
        password: "12345",
        name: '',
        groups: ['group1', 'group2']
    }
];

export function getUsers() {
    return users;
}

export function getUser(login) {
    const user = users.find(item => item.id === login);
    user.groups = user.groups.map(getGroup);  
    return user;
}
