import { getUsers } from "./accounts";

const groups = [
    {
        name: 'account1',
        owner: 'user1',
        
    }
];

export function getGroups() {
    return groups;
}

export function getGroup(groupName) {
    const group = groups.find(group => group.name === groupName);
    const users = getUsers();
    group.users = users.filter(user => user.groups.includes(groupName))
}
