import { getGroupById, getGroups } from "./groups";

const accounts = [
    {
        id: 'user1',
        login: 'account1',
        password: "12345",
        groupIds: ['group1', 'group2']
    }
];

export function getAccounts() {
    return accounts;
}

export function getAccount(accountId) {
    const account = accounts.find(account => account.id === accountId);
    account.groups = account.groupIds.map(getGroup);  
    return account;
}

function getOwnedGroups(accountId) {
    const groups = getGroups();
    return groups.filter(group => group.ownerId === accountId);
}
